mapper.select:
SELECT q.*, q.rewards as r_value, qo.id as qo_id, qo.quest_id as qo_quest_id, qo.person_id as qo_person_id, qo.rewards as qo_r_value, qo.state as qo_state FROM quest q LEFT JOIN quest_order qo ON q.id = qo.quest_id WHERE true
AND q.id = #{questId}
AND q.is_closed = false AND q.world_id = #{worldId}
AND (qo.state = 'ONGOING' OR qo.state IS NULL)
ORDER BY q.id

mapper.update:
UPDATE quest
SET processed_at = #{processedAt},
    is_closed    = #{isClosed}
WHERE id = #{id}
