mapper.select:
SELECT qo.*, qop.game_date as qop_game_date, qop.progress as qop_progress, qor.game_date as qor_game_date, qor.succeeded as qor_succeeded FROM quest_order qo INNER JOIN quest q on q.id = qo.quest_id LEFT JOIN quest_order_progress qop on qo.id = qop.quest_order_id LEFT JOIN quest_order_result qor on qo.id = qor.quest_order_id WHERE q.world_id = #{worldId}
AND qop.game_date = #{gameDate}
AND qor.succeeded IS NULL

mapper.delete:
DELETE
FROM quest_order qo
WHERE id = #{id};
